import type { Locale, RequiredLocale } from '@ant-design/web3-common';
type ComponentName = keyof Locale;
interface IntlType<T extends ComponentName = any> {
    messages: RequiredLocale[T];
    getMessage: (message: string, values?: Record<string, string>) => string;
}
export default function useIntl<T extends ComponentName = any>(componentName: T, componentLocale?: Locale[T]): IntlType<T>;
export { IntlType };
